export function hilightFactory(icon, height, width) {
	if (icon.startX > icon.endX) {
		icon.endX = [ icon.startX, (icon.startX = icon.endX) ][0];
	}
	if (icon.startY > icon.endY) {
		icon.endY = [ icon.startY, (icon.startY = icon.endY) ][0];
	}
	let startX = icon.startX / 10000 * width;
	let startY = icon.startY / 10000 * height;
	let endX = icon.endX / 10000 * width;
	let endY = icon.endY / 10000 * height;
	return (
		<rect
			key={Math.random()}
			x={startX}
			y={startY}
			width={endX - startX}
			height={endY - startY}
			fill="yellow"
			opacity="0.3"
		/>
	);
}
export function arrowFactory(icon, height, width) {
	let startX = icon.startX / 10000 * width;
	let startY = icon.startY / 10000 * height;
	let endX = icon.endX / 10000 * width;
	let endY = icon.endY / 10000 * height;
	return <line key={Math.random()} x1={startX} y1={startY} x2={endX} y2={endY} stroke="red" strokeWidth={5} />;
}
