import React, { Component } from 'react';
import { Document, Page } from 'react-pdf';
import { pdfjs } from 'react-pdf';
import Drawing from './drawing';

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

export default class Display extends Component {
	state = {
		SVGs: [
			{ type: 'hilight', startX: 1000, startY: 1000, endX: 2000, endY: 2000 },
			{ type: 'line', startX: 2000, startY: 5000, endX: 8000, endY: 2000 }
		],
		shadow: null,
		height: null,
		width: null
	};
	setDrawing = () => {
		let page = document.querySelector('.display canvas');
		this.setState({ height: page.clientHeight, width: page.clientWidth });
	};
	handleClick = (e) => {
		this.setState({
			shadow: {
				type: 'hilight',
				startX: e.nativeEvent.x / this.state.width * 10000,
				startY: e.nativeEvent.y / this.state.height * 10000,
				endX: 2000,
				endY: 2000
			}
		});
	};
	render() {
		return (
			<Document className="display" file={this.props.content}>
				<Page
					onRenderSuccess={this.setDrawing}
					height={window.innerHeight - 150}
					pageNumber={this.props.pageNumber}
				/>
				<Drawing
					SVGs={this.state.SVGs}
					onClick={this.handleClick}
					onDrag={this.handleClick}
					height={this.state.height}
					width={this.state.width}
				/>
			</Document>
		);
	}
}
