import React, { Component } from 'react';
import { Document, Page } from 'react-pdf';
import { pdfjs } from 'react-pdf';
import SVG from 'svg.js';

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;
function drawSVG(SVGs, height, width) {
	for (let icon of SVGs) {
		icon.startX = icon.startX / 10000 * height;
		icon.startY = icon.startY / 10000 * width;
		icon.endX = icon.endX / 10000 * height;
		icon.endY = icon.endY / 10000 * width;
		let draw = SVG('SVG');
		draw
			.rect(icon.endX - icon.startX, icon.endY - icon.startY)
			.move(icon.startX, icon.startY)
			.attr({ fill: '#f06' });
	}
	console.log(height, width);
}
export default class Display extends Component {
	state = {
		SVGs: [ { type: 'hilight', startX: 0, startY: 0, endX: 5000, endY: 5000 } ]
	};
	setSVG = () => {
		let page = document.querySelector('.display canvas');
		let svg = document.querySelector('#SVG');
		svg.style.height = page.clientHeight;
		svg.style.width = page.clientWidth;
		drawSVG(this.state.SVGs, page.clientHeight, page.clientWidth);
	};
	render() {
		return (
			<Document className="display" file={this.props.content}>
				<Page
					onRenderSuccess={this.setSVG}
					height={window.innerHeight - 150}
					pageNumber={this.props.pageNumber}
				/>
				<svg id="SVG" />
			</Document>
		);
	}
}
