import React, { Component } from 'react';
function hilightFactory(icon, height, width) {
	if (icon.startX > icon.endX) {
		icon.endX = [ icon.startX, (icon.startX = icon.endX) ][0];
	}
	if (icon.startY > icon.endY) {
		icon.endY = [ icon.startY, (icon.startY = icon.endY) ][0];
	}
	let startX = icon.startX / 10000 * width;
	let startY = icon.startY / 10000 * height;
	let endX = icon.endX / 10000 * width;
	let endY = icon.endY / 10000 * height;
	return (
		<rect
			key={Math.random()}
			x={startX}
			y={startY}
			width={endX - startX}
			height={endY - startY}
			fill="yellow"
			opacity="0.3"
		/>
	);
}
function lineFactory(icon, height, width) {
	if (icon.startX > icon.endX) {
		icon.endX = [ icon.startX, (icon.startX = icon.endX) ][0];
	}
	if (icon.startY > icon.endY) {
		icon.endY = [ icon.startY, (icon.startY = icon.endY) ][0];
	}
	let startX = icon.startX / 10000 * width;
	let startY = icon.startY / 10000 * height;
	let endX = icon.endX / 10000 * width;
	let endY = icon.endY / 10000 * height;
	return (
		<rect
			key={Math.random()}
			x={startX}
			y={startY}
			width={endX - startX}
			height={endY - startY}
			fill="yellow"
			opacity="0.3"
		/>
	);
}
class Drawing extends Component {
	state = {};
	render() {
		let divStyle = {
			height: this.props.height,
			width: this.props.width
		};
		let icons = [];
		for (let icon of this.props.SVGs) {
			if (icon.type == 'hilight') {
				icons.push(hilightFactory(icon, this.props.height, this.props.width));
			}
		}
		return (
			<svg className="drawing" style={divStyle}>
				{icons}
			</svg>
		);
	}
}

export default Drawing;
