import React, { Component } from 'react';
class Drawing extends Component {
	state = {};
	render() {
		let divStyle = {
			height: this.props.height,
			width: this.props.width
		};
		let icons = [];
		for (let icon of this.props.SVGs) {
			if (icon.startX > icon.endX) {
				icon.endX = [ icon.startX, (icon.startX = icon.endX) ][0];
			}
			if (icon.startY > icon.endY) {
				icon.endY = [ icon.startY, (icon.startY = icon.endY) ][0];
			}
			icon.startX = icon.startX / 10000 * this.props.width;
			icon.startY = icon.startY / 10000 * this.props.height;
			icon.endX = icon.endX / 10000 * this.props.width;
			icon.endY = icon.endY / 10000 * this.props.height;
			icons.push(
				<rect
					key={icon.endY}
					x={icon.startX}
					y={icon.startY}
					width={icon.endX - icon.startX}
					height={icon.endY - icon.startY}
					style="fill:rgb(0,0,255);stroke-width:3;stroke:rgb(0,0,0)"
				/>
			);
		}
		console.log(icons);
		return (
			<svg className="drawing" style={divStyle}>
				{icons}
			</svg>
		);
	}
}

export default Drawing;
