import React, { Component } from 'react';
class Drawing extends Component {
	state = {};
	render() {
		let divStyle = {
			height: this.props.height,
			width: this.props.width
		};
		let icons = [];
		for (let icon of this.props.SVGs) {
			if (icon.startX > icon.endX) {
				icon.endX = [ icon.startX, (icon.startX = icon.endX) ][0];
			}
			if (icon.startY > icon.endY) {
				icon.endY = [ icon.startY, (icon.startY = icon.endY) ][0];
			}
			let startX = icon.startX / 10000 * this.props.width;
			let startY = icon.startY / 10000 * this.props.height;
			let endX = icon.endX / 10000 * this.props.width;
			let endY = icon.endY / 10000 * this.props.height;
			icons.push(
				<rect
					key={Math.random()}
					x={startX}
					y={startY}
					width={endX - startX}
					height={endY - startY}
					fill="yellow"
					opacity="0.3"
				/>
			);
		}
		console.log(this.props.SVGs);
		return (
			<svg className="drawing" style={divStyle}>
				{icons}
			</svg>
		);
	}
}

export default Drawing;
