import React, { Component } from 'react';
import SVG from 'svg.js';
function drawSVG(SVGs, height, width) {
	for (let icon of SVGs) {
		if (icon.startX > icon.endX) {
			icon.endX = [ icon.startX, (icon.startX = icon.endX) ][0];
		}
		if (icon.startY > icon.endY) {
			icon.endY = [ icon.startY, (icon.startY = icon.endY) ][0];
		}
		icon.startX = icon.startX / 10000 * width;
		icon.startY = icon.startY / 10000 * height;
		icon.endX = icon.endX / 10000 * width;
		icon.endY = icon.endY / 10000 * height;
		let draw = SVG('SVG');
		draw
			.rect(icon.endX - icon.startX, icon.endY - icon.startY)
			.move(icon.startX, icon.startY)
			.attr({ fill: '#f06' });
	}
}
class Svg extends Component {
	state = {};
	render() {
		return <svg id="SVG" />;
	}
}

export default Svg;
